const math = require('mathjs');

const generate = (usePredefinedValues = 0) => {
    // Predefined values for testing
    const predefinedValues = {
        m1_mass: 5, // mass of block 1 in kg
        m2_mass: 10, // mass of block 2 in kg
        theta: 30, // angle in degrees
        mu_s: 0.5, // static friction coefficient
        mu_k: 0.3 // kinetic friction coefficient
    };

    // 1. Dynamic Parameter Selection
    const angle = usePredefinedValues ? predefinedValues.theta : math.randomInt(15, 46); // Random angle between 15 and 45 degrees
    const m1 = usePredefinedValues ? predefinedValues.m1_mass : math.randomInt(1, 11); // Random m1 mass between 1 and 10 kg
    const m2 = usePredefinedValues ? predefinedValues.m2_mass : math.randomInt(5, 21); // Random m2 mass between 5 and 20 kg
    const mu_s = usePredefinedValues ? predefinedValues.mu_s : math.round(math.random() * 1, 2); // Random mu_s between 0 and 1
    const mu_k = usePredefinedValues ? predefinedValues.mu_k : math.round(math.random() * 1, 2); // Random mu_k 

    // 2. Solution Synthesis
    const g = 9.81; // Gravity in m/s²
    const thetaRad = math.unit(angle, 'deg').toNumber('rad'); // Convert angle to radians

    // Calculate forces
    const F2 = m2 * g; // Force due to weight of block 2 (F2 = m2 * g)
    const F1 = m1 * g * math.sin(thetaRad); // Force due to weight component of block 1 along incline (F1 = m1 * g * sin(theta))
    const F_friction = mu_k * m1 * g * math.cos(thetaRad); // Frictional force (F_friction = mu_k * m1 * g * cos(theta))

    // Applying Newton's second law 
    const F_net = F2 - (F1 + F_friction); // Net force acting on m2
    const acceleration = F_net / (m1 + m2); // a = F_net / (m1 + m2)

    // Return the structured data
    return {
        params: {
            m1: m1,
            m2: m2,
            theta: angle,
            angle: angle,
            mu_s: mu_s,
            mu_k: mu_k,
            m1_mass: m1,
            m2_mass: m2,
            intermediate: {
                thetaRad: math.round(thetaRad, 3),
                F1: math.round(F1, 3),
                F_friction: math.round(F_friction, 3),
                F_net: math.round(F_net, 3)
            }
        },
        correct_answers: {
            accel: math.round(acceleration, 3) // Round to 3 decimal places
        },
        nDigits: 3,
        sigfigs: 3
    };
};

module.exports = { generate };
